import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './MyComponents/Header';
import Todos from './MyComponents/Todos';
import AddTodo from './MyComponents/AddTodo';
import About from './MyComponents/About';
import Footer from './MyComponents/Footer';
import './App.css';

function App() {
  const [darkMode, setDarkMode] = useState(() => {
    const savedTheme = localStorage.getItem("darkMode");
    return savedTheme ? JSON.parse(savedTheme) : false;
  });

  // Add theme effect
  useEffect(() => {
    localStorage.setItem("darkMode", JSON.stringify(darkMode));
    if (darkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
  }, [darkMode]);

  const toggleTheme = () => {
    setDarkMode(!darkMode);
  };
  
  const [todos, setTodos] = useState(() => {
    const savedTodos = localStorage.getItem("todos");
    return savedTodos ? JSON.parse(savedTodos) : [];
  });

  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  const filteredTodos = todos.filter((todo) => {
    const matchesSearch = todo.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      todo.desc.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === "all" || todo.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  // Sort todos by due date
  const sortedTodos = [...filteredTodos].sort((a, b) => {
    if (!a.dueDate) return 1;
    if (!b.dueDate) return -1;
    return new Date(a.dueDate) - new Date(b.dueDate);
  });

  // Update addTodo to include category and dueDate
  // Calculate progress
  const completedTodos = todos.filter((todo) => todo.completed).length;
  const progress = todos.length > 0 ? Math.round((completedTodos / todos.length) * 100) : 0;
  
  // Update addTodo to include completed status
  const addTodo = (title, desc, category, dueDate) => {
    let sno = todos.length > 0 ? todos[todos.length - 1].sno + 1 : 1;
    const myTodo = {
      sno,
      title,
      desc,
      category: category || "personal",
      dueDate: dueDate || null,
      completed: false
    };
    setTodos([...todos, myTodo]);
  };

  // Add toggleComplete function
  const toggleComplete = (sno) => {
    setTodos(
      todos.map((todo) =>
        todo.sno === sno ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  // Add the missing onDelete function
  const onDelete = (todo) => {
    setTodos(todos.filter((e) => e !== todo));
  };

  // Add the missing editTodo function
  const editTodo = (sno, updatedTitle, updatedDesc, updatedDueDate) => {
    setTodos(
      todos.map((todo) =>
        todo.sno === sno ? 
        { ...todo, title: updatedTitle, desc: updatedDesc, dueDate: updatedDueDate } 
        : todo
      )
    );
  };

  return (
    <Router>
      <div className={darkMode ? 'dark-mode' : 'light-mode'}>
        <Header 
          title="TaskFlow" 
          searchTerm={searchTerm} 
          setSearchTerm={setSearchTerm}
          selectedCategory={selectedCategory}
          setSelectedCategory={setSelectedCategory}
          darkMode={darkMode}
          toggleTheme={toggleTheme}
          progress={progress}
        />
        <Routes>
          <Route
            exact
            path="/"
            element={
              <>
                <AddTodo addTodo={addTodo} />
                <Todos 
                  todos={sortedTodos} 
                  onDelete={onDelete} 
                  onEdit={editTodo}
                  onToggleComplete={toggleComplete}
                  selectedCategory={selectedCategory}
                />
              </>
            }
          />
          <Route exact path="/about" element={<About />} />
        </Routes>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
