import React, { useState } from 'react';

const TodoItem = ({todo, onDelete, onEdit, onToggleComplete}) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(todo.title);
  const [editDesc, setEditDesc] = useState(todo.desc);
  const [editDueDate, setEditDueDate] = useState(todo.dueDate || '');

  const handleEdit = () => {
    onEdit(todo.sno, editTitle, editDesc, editDueDate);
    setIsEditing(false);
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const getCategoryBadgeColor = (category) => {
    switch(category) {
      case 'work': return 'bg-primary';
      case 'urgent': return 'bg-danger';
      case 'personal': return 'bg-success';
      default: return 'bg-secondary';
    }
  };

  return (
    <div className={`card my-2 shadow-sm ${todo.completed ? 'border-success' : ''}`}>
      <div className="card-body">
        {!isEditing ? (
          <>
            <div className="d-flex justify-content-between align-items-center">
              <div className="d-flex align-items-center">
                <input
                  type="checkbox"
                  className="form-check-input me-3"
                  checked={todo.completed}
                  onChange={() => onToggleComplete(todo.sno)}
                />
                <h4 className={`card-title mb-0 ${todo.completed ? 'text-decoration-line-through' : ''}`}>
                  {todo.title}
                </h4>
              </div>
              <div>
                <span className={`badge ${getCategoryBadgeColor(todo.category)} me-2`}>
                  {todo.category}
                </span>
                {todo.dueDate && (
                  <span className="badge bg-info">
                    Due: {formatDate(todo.dueDate)}
                  </span>
                )}
              </div>
            </div>
            <p className={`card-text ${todo.completed ? 'text-decoration-line-through' : ''}`}>
              {todo.desc}
            </p>
            <div className="btn-group">
              <button className="btn btn-sm btn-primary me-2" onClick={() => setIsEditing(true)}>
                Edit
              </button>
              <button className="btn btn-sm btn-danger" onClick={() => onDelete(todo)}>
                Delete
              </button>
            </div>
          </>
        ) : (
          <form onSubmit={(e) => { e.preventDefault(); handleEdit(); }}>
            <div className="mb-3">
              <input
                type="text"
                className="form-control"
                value={editTitle}
                onChange={(e) => setEditTitle(e.target.value)}
                placeholder="Title"
              />
            </div>
            <div className="mb-3">
              <input
                type="text"
                className="form-control"
                value={editDesc}
                onChange={(e) => setEditDesc(e.target.value)}
                placeholder="Description"
              />
            </div>
            <div className="mb-3">
              <input
                type="date"
                className="form-control"
                value={editDueDate}
                onChange={(e) => setEditDueDate(e.target.value)}
              />
            </div>
            <button type="submit" className="btn btn-sm btn-success me-2">
              Save
            </button>
            <button
              type="button"
              className="btn btn-sm btn-secondary"
              onClick={() => setIsEditing(false)}
            >
              Cancel
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default TodoItem;
