import React from 'react';
import TodoItem from './TodoItem';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const Todos = (props) => {
  const onDragEnd = (result) => {
    if (!result.destination) return;
    
    const items = Array.from(props.todos);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);
    
    props.reorderTodos(items);
  };

  return (
    <div className='container'>
      <h3 className='my-4'>Todos List</h3>
      {props.todos.length === 0 ? (
        <div className="alert alert-info">No todos to display</div>
      ) : (
        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="todos">
            {(provided) => (
              <div {...provided.droppableProps} ref={provided.innerRef}>
                {props.todos.map((todo, index) => (
                  <Draggable key={todo.sno} draggableId={todo.sno.toString()} index={index}>
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        <TodoItem
                          todo={todo}
                          onDelete={props.onDelete}
                          onEdit={props.onEdit}
                          onToggleComplete={props.onToggleComplete}
                        />
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>
      )}
    </div>
  );
};

export default Todos;
