import React, { useState } from 'react';

const TodoItem = ({todo, onDelete, onEdit}) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(todo.title);
  const [editDesc, setEditDesc] = useState(todo.desc);

  const handleEdit = () => {
    onEdit(todo.sno, editTitle, editDesc);
    setIsEditing(false);
  };

  const getCategoryBadgeColor = (category) => {
    switch(category) {
      case 'work': return 'bg-primary';
      case 'urgent': return 'bg-danger';
      case 'personal': return 'bg-success';
      default: return 'bg-secondary';
    }
  };

  return (
    <div className="card my-2 shadow-sm">
      <div className="card-body">
        {!isEditing ? (
          <>
            <div className="d-flex justify-content-between align-items-center">
              <h4 className="card-title">{todo.title}</h4>
              <span className={`badge ${getCategoryBadgeColor(todo.category)}`}>
                {todo.category}
              </span>
            </div>
            <p className="card-text">{todo.desc}</p>
            <div className="btn-group">
              <button className="btn btn-sm btn-primary me-2" onClick={() => setIsEditing(true)}>
                Edit
              </button>
              <button className="btn btn-sm btn-danger" onClick={() => onDelete(todo)}>
                Delete
              </button>
            </div>
          </>
        ) : (
          <form onSubmit={(e) => { e.preventDefault(); handleEdit(); }}>
            <div className="mb-3">
              <input
                type="text"
                className="form-control"
                value={editTitle}
                onChange={(e) => setEditTitle(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <input
                type="text"
                className="form-control"
                value={editDesc}
                onChange={(e) => setEditDesc(e.target.value)}
              />
            </div>
            <button type="submit" className="btn btn-sm btn-success me-2">
              Save
            </button>
            <button
              type="button"
              className="btn btn-sm btn-secondary"
              onClick={() => setIsEditing(false)}
            >
              Cancel
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default TodoItem;
